drop database CakesDB
go

create database CakesDB
go

use CakesDB

create table Shape
(
Shape_ID int IDENTITY(1,1) primary key,
Shapes varchar(50)
)

create table cake_Type
(
[Type_ID] int IDENTITY(1,1) primary key,
[Types] varchar (50)
)

create table Size
(
Size_ID int IDENTITY(1,1) primary key,
Sizes varchar (50)
)

create table Flavour
(
Flavour_ID int IDENTITY(1,1) primary key,
Flavours varchar(20)
)

create table Cake
(
ID int IDENTITY(1,1) primary key,
Name varchar (50),
Ingridents varchar(500),
Method varchar(1000),
Cake_Type int references cake_Type([Type_ID]),
Cake_Flavour int references Flavour(Flavour_ID),
Cake_shape int references Shape(Shape_ID),
Cake_Size int references Size(Size_ID)
)

